import rnaToProtein

rtp = rnaToProtein.rnaToProtein

def peptideEncode(dna, protein):
    finalList = []
    prolen = len(protein)
    for i in range(len(dna)-(prolen*3)):
        strand = dna[i:i+(prolen*3)]
        newstr = strand.replace('T', 'U')
        revstr = reverseComp(strand).replace('T', 'U')
        if rtp(newstr) == protein or rtp(revstr) == protein:
            finalList.append(strand)
    return finalList

def reverseComp(dna):
    new = ""
    for char in dna:
        if char == 'A':
            new += 'T'
        if char == 'T':
            new += 'A'
        if char == 'C':
            new += 'G'
        if char == 'G':
            new += 'C'

    return new[::-1]

