from Bio import SeqIO
import random
import gibbs
import greedyPsuedocount
import pandas

gibbsSampler = gibbs.gibbsSampler
score = greedyPsuedocount.score
consensus = greedyPsuedocount.consensus

def repeatGibbs(dna, k, t, n):
    best_motifs = []
    for i in range(t):
        rand = random.randrange(len(dna[0]) - k)
        best_motifs.append(dna[i][rand:rand + k])
    best_score = score(best_motifs)
    for i in range(0, 2000):
        motifs = gibbsSampler(dna, k, t, n)
        if score(motifs) < score(best_motifs):
            best_motifs = motifs[:]
            best_score = score(motifs)
    return best_motifs, best_score

def get_data_random(filename):
    df = pandas.read_csv(filename)
    df2 = df.set_index("CLASS")
    data = df2.loc["toxic", "DNASEQ"]

    sample = random.sample(range(len(data)), 400)
    dna = []
    for rand in sample:
        dna.append(data.iloc[rand])
    return dna

